CREATE TABLE `faculty_other_groups` (
  `fog_id` int(11) NOT NULL AUTO_INCREMENT,
  `fog_fac_username` varchar(100) NOT NULL,
  `fog_grp_code` varchar(20) NOT NULL,
  PRIMARY KEY (`fog_id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

CREATE TABLE `faculty_section_groups` (
  `fsg_id` int(11) NOT NULL AUTO_INCREMENT,
  `fsg_fac_username` varchar(100) NOT NULL,
  `fsg_ss_code` varchar(20) NOT NULL,
  PRIMARY KEY (`fsg_id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=latin1;

CREATE TABLE `groups` (
  `grp_code` varchar(20) NOT NULL,
  `grp_name` varchar(100) NOT NULL,
  PRIMARY KEY (`grp_code`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;
